import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    container: {
        position: 'relative',
        border: '1px solid #5656ef',
        borderRadius: 6,
        width: 'auto',
        minWidth: 150,
        maxWidth: 350,
        minHeight: 50,
        padding: 10,
        '&:hover': {
            border: '2px solid #5656ef',
        }
    },
    value: {
        fontSize: 20,
        color: '#5656ef',
        fontWeight: 500
    },
    valuesContainer: {
        position: 'absolute',
        width: '90%',
        bottom: -70,
        backgroundColor: '#FFF',
        borderRadius: 6,
        boxShadow: '0px 0px 6px'
    },
    valueItem: {
        width: '100%',
        margin: '10px 0',
        padding: 20,
        '&:hover': {
            backgroundColor: '#dfdfff'
        }
    }
}))

export default useStyles
