import React, { useEffect, useRef } from 'react'

import { Grid } from '@material-ui/core'

import useStyles from './MenuMobile.styles'

const MenuMobile = (props: any) => {
    const classes = useStyles()

    const {
        isOpen
    } = props

    const menuContainer = useRef(null) as any

    useEffect(() => {
        if (menuContainer && menuContainer.current) {
            console.log(menuContainer.current)
        }
    }, [isOpen])

    return (
        <Grid ref={menuContainer} container className={classes.menuContainer}>

        </Grid>
    )
}

export default MenuMobile
