import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles({
    navContainer: {
        width: '100%'
    },
    navItem: {
        width: 150,
        position: 'relative',
        '& a': {
            textDecoration: 'none'
        }
    },
    navBorder: {
        position: 'absolute',
        bottom: -30,
        width: '100%',
        height: 10,
        backgroundColor: '#5656ef',
        borderRadius: 10
    }
})

export default useStyles
