import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        minHeight: '100vh',
        flexDirection: 'column'
    },
    webBlock: {
        flex: 1,
        margin: '50px 0'
    },
    switchWrapper: {
        position: 'relative',
        maxHeight: '100vh',
        '& > div': {
            position: 'absolute',
            maxHeight: '100vh'
        }
    }
}))

export default useStyles
