import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles({
    navContainer: {
        width: '100%',
        position: 'relative'
    },
    navItem: {
        width: 150,
        '& a': {
            textDecoration: 'none'
        }
    },
    navBorder: {
        position: 'absolute',
        bottom: -20,
        width: 150,
        height: 10,
        left: 0
    },
    navBorderDash: {
        width: '90%',
        height: '100%',
        backgroundColor: '#5656ef',
        borderRadius: 10,
        margin: '0 auto'
    }
})

export default useStyles
