import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    container: {
        border: '1px solid #5656ef',
        borderRadius: 6,
        width: 'auto',
        minWidth: 150,
        maxWidth: 350,
        minHeight: 50,
        padding: 10,
        '&:hover': {
            minHeight: 49,
            minWidth: 149,
            border: '2px solid #5656ef',
        }
    },
    input: {
    }
}))

export default useStyles
