import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    infoCard: {
        width: '100%',
        borderRadius: 10,
        boxShadow: '0px 5px 20px 0px grey',
        color: '#5656ef',
        backgroundColor: '#dfdfff',
        padding: 30
    },
    title: {
        fontSize: 34,
        fontWeight: 600
    },
    text: {
        fontSize: 18,
        textAlign: 'left'
    }
}))

export default useStyles
