import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    container: {
        border: '1px solid #5656ef',
        borderRadius: 6,
        minWidth: 150,
        maxWidth: 350,
        minHeight: 50,
        padding: 20,
        '&:hover': {
            border: '2px solid #5656ef',
        }
    },
    input: {
        border: 0,
        width: '100%',
        height: '100%'
    }
}))

export default useStyles
