import * as React from 'react'
import { useState, useEffect, useRef } from 'react'
import { useLocation } from 'react-router-dom'

import { Link } from 'react-router-dom'

import { Grid, Typography } from '@material-ui/core'

import useStyles from './Navigation.styles'


const Navigation = (props: any) => {
    const classes = useStyles()
    const location = useLocation()

    const itemWidth = 150

    const navBorder = useRef(null) as any

    const [current, setCurrent] = useState(1)
    const [currentBorderPosition, setCurrentBorderPosition] = useState(0)

    useEffect(() => {
        if (navBorder && navBorder.current) {
            console.log(current * itemWidth)
            navBorder.current.style.left = `${current * itemWidth} px`
        }
    }, [current])

    useEffect(() => {
        if (location.pathname === '/') {
            setCurrent(0)
        } else if (location.pathname === '/faqs') {
            setCurrent(1)
        }
    }, [location.pathname])

    return (
        <Grid container justify='space-around' className={classes.navContainer}>
            <Grid item className={classes.navItem}>
                <Link to='/'>Home</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/faqs'>FAQS</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <div ref={navBorder} className={classes.navBorder}>
                <div className={classes.navBorderDash} />
            </div>
        </Grid>
    )
}

export default Navigation
