import React, { useEffect, useRef } from 'react'

import { withRouter, Route } from 'react-router-dom'
import { AnimatedSwitch } from 'react-router-transition'

import { Grid } from '@material-ui/core'

import routes from '../../router/RouterRutes'

import Footer from '../footer/Footer'

import useStyles from './WebContainer.styles'

const WebContainer = (props: any) => {
    const classes = useStyles()

    const routeContainer = useRef(null) as any

    useEffect(() => {
        if (routeContainer && routeContainer.current) {
            console.log(routeContainer.current)
        }
    }, [routeContainer])

    return (
        <Grid className={classes.background}>
            <Grid container justify='center' className={classes.webBlock}>
                <Grid item lg={8} md={11} sm={11} xs={11}>
                    <AnimatedSwitch
                        atEnter={{ opacity: 0 }}
                        atLeave={{ opacity: 0 }}
                        atActive={{ opacity: 1 }}
                        className={classes.switchWrapper}
                        ref={routeContainer}
                    >
                        {
                            routes.map(route => (
                                <Route exact path={route.path} component={route.component} />
                            ))
                        }
                    </AnimatedSwitch>
                </Grid>
            </Grid>
        </Grid>
    )
}

export default withRouter(WebContainer)
