import React from 'react'

import { withRouter, Switch, Route, useLocation } from 'react-router-dom'
import { TransitionGroup, CSSTransition } from 'react-transition-group'

import { Grid } from '@material-ui/core'
import Wrapper from './Wrapper'

import routes from '../../router/RouterRutes'

import useStyles from './WebContainer.styles'

const WebContainer = (props: any) => {
    const classes = useStyles()
    const location = useLocation()

    return (
        <Grid container justify='center' className={classes.webBlock}>
            <Grid item md={8} sm={10} xs={11}>
                <Wrapper>
                    <TransitionGroup className={classes.transitionGroup}>
                        <CSSTransition
                            key={location.key}
                            timeout={{ enter: 300, exit: 300 }}
                            classNames={'fade'}
                        >
                            <section className={classes.routeSection}>
                                <Switch location={location}>
                                    {
                                        routes.map(route => (
                                            <Route exact path={route.path} component={route.component} />
                                        ))
                                    }
                                </Switch>
                            </section>
                        </CSSTransition>
                    </TransitionGroup>
                </Wrapper>
            </Grid>
        </Grid>
    )
}

export default withRouter(WebContainer)
