import * as React from 'react'
import { useState, useEffect, useRef } from 'react'

import { Link } from 'react-router-dom'

import { Grid, Typography } from '@material-ui/core'

import useStyles from './Navigation.styles'


const Navigation = (props: any) => {
    const classes = useStyles()

    const itemWidth = 150

    const navBorder = useRef()

    const [current, setCurrent] = useState(0)
    const [currentBorderPosition, setCurrentBorderPosition] = useState(0)

    useEffect(() => {

    }, [current])

    useEffect(() => {
        if (navBorder && navBorder.current) {
            navBorder.current.left = current * itemWidth
        }
    }, [currentBorderPosition])

    return (
        <Grid container justify='space-around' className={classes.navContainer}>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <Grid item className={classes.navItem}>
                <Link to='/'>Prueba</Link>
            </Grid>
            <div ref={navBorder} className={classes.navBorder}>
                <div className={classes.navBorderDash} />
            </div>
        </Grid>
    )
}

export default Navigation
