import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme) => ({
    '@keyframes slide-in': {
        '100%': { transform: 'translateX(0%)' }
    },
    '@-webkit-keyframes slide-in': {
        '100%': { '-webkit-transform': 'translateX(0%)' }
    },
    '@keyframes slide-out': {
        '0%': { transform: 'translateX(0%)' },
        '100%': { transform: 'translateX(-110%)' }
    },
    '@-webkit-keyframes slide-out': {
        '0%': { '-webkit-transform': 'translateX(0%)' },
        '100%': { '-webkit-transform': 'translateX(-110%)' }
    },
    menuContainer: {
        position: 'absolute',
        width: '50%',
        height: 800,
        backgroundColor: '#FFF',
        boxShadow: '0px 5px 20px 0px grey',
        transform: 'translateX(-110%)',
        '-webkit-transform': 'translateX(-110%)'
    },
    slideIn: {
        animation: `$slide-in 0.5s ${theme.transitions.easing.easeIn}`,
        'animation-fill-mode': 'forwards'
    },
    slideOut: {
        animation: `$slide-out 0.5s ${theme.transitions.easing.easeOut}`,
        'animation-fill-mode': 'forwards'
    },
    navContainer: {
        position: 'relative'
    },
    navItem: {
        height: 75,
        backgroundColor: '#fff',
        transition: 'background-color 0.5s',
        borderRadius: 5,
        padding: '5px 0',
        '& a': {
            fontSize: 20,
            textDecoration: 'none',
            color: '#5656ef',
            '&:visited': {
                color: '#5656ef'
            }
        }
    },
    navItemActive: {
        backgroundColor: '#dfdfff',
        fontWeight: 500
    },
    navBorder: {
        position: 'absolute',
        bottom: 45,
        width: 150,
        height: 5,
        left: 0,
        transition: 'left 0.5s'
    },
    navBorderDash: {
        width: '100%',
        height: '100%',
        backgroundColor: '#5656ef',
        borderRadius: 10,
        margin: '0 auto'
    }
}))

export default useStyles
